import ReactDOM from "react-dom";
import React, { Component } from "react";
import { w3cwebsocket as W3CWebSocket } from "websocket";
import { message } from "antd";

const client = new W3CWebSocket('wss://salty-dawn-74239.herokuapp.com');

export default class App extends Component  {

    onButtonClicked = (value) => {
        client.send(JSON.stringify({
            type: "message",
            msg: value
        }));
    }

    componentDidMount(){
        client.onopen = () => {
            console.log("WebSocket Client Connected!");
        };
        client.onmessage = (message) => {
            //const dataFromServer = JSON.parse(message.data);
            console.log('got reply: ', message.data);
        }

    }
    render() {
        return (
            <div>
                <button onClick={() => this.onButtonClicked("Hello server!")}> Send Message </button>

                <div className='button-container'>
                    <Button isAuto={true} />
                    <Button isAuto={false} btnIconType='up'/>
                    <Button isAuto={false} btnIconType='down'/>
                </div>
            </div>
        )
    }
}

function Button({ isAuto, btnIconType }){

    return(
      <>
        <button>
          {isAuto
            ? 'Auto'
            : (
              btnIconType === 'up' 
              ? <AiOutlineArrowUp />
              : <AiOutlineArrowDown />
            )}
        </button>
      </>
    )
  }

ReactDOM.render(<App />, document.getElementById('root'));
